var router = function(app){

    if(typeof app == "undefined"){
        throw new Error("App in undefined");
    }

    var _this = this;

    this._app = app;

    this.option = false; //default component

    this.controller = 'main'; //default controller

    this.method = 'index'; //default method

    this.request = {};

    this.response = {};

    this.execute = function(req,res){
        this.fetchUrl(req,res,function(){

        });
    }

    this.fetchUrl = function(req,res,next){
        _this.request = req;
        _this.response = res;
        _this.controller = 'main';
        _this.method = 'index';
        _this.option = false;
        if(typeof next == "function"){
            next();
        }
    }


    return this;
}

module.exports = router;